public class Part1 {
  public String findSimpleGene(String dna) {
    int startIndex = dna.indexOf("atg");
    int stopIndex = dna.indexOf("taa", startIndex+3);
    if (startIndex == -1 || stopIndex == -1) {return "";}
    String gene = dna.substring(startIndex, stopIndex+3);
    if (gene.length()%3 == 0) {return gene;}
    return "";
  }

  public void testSimpleGene() {
    String dna01 = "nevknwl";
    String dna02 = "atgnevknwl";
    String dna03 = "nevknwltaa";
    String dna04 = "svaatg123taade";
    String dna05 = "svaatg1234taade";
    String dna06 = "";

    System.out.println((dna01));
    System.out.println(findSimpleGene(dna01));
    System.out.println((dna02));
    System.out.println(findSimpleGene(dna02));
    System.out.println((dna03));
    System.out.println(findSimpleGene(dna03));
    System.out.println((dna04));
    System.out.println(findSimpleGene(dna04));
    System.out.println((dna05));
    System.out.println(findSimpleGene(dna05));
    System.out.println((dna06));
    System.out.println(findSimpleGene(dna06));
  }
}
